import React, { useState , useEffect} from 'react'
import axios from 'axios'

export default function Comp() {
    const [s,setS] = useState(null)
    
    useEffect(()=>{
        //use fetch by Promise way
        fetch('https://jsonplaceholder.typicode.com/users/1')
        .then(res=>res.json())
        .then(data=>setS(data))
        .catch(err=>console.log('erorr :' , err))

        //use fetch by async await
        const f = async()=>{
            try{
                const response = await fetch('https://jsonplaceholder.typicode.com/users/1')
                const data = await response.json()
                setS(data)
            }catch(err){console.log('erorr :' , err)}
        };f()

        // use axios by Promise way
        axios.get('https://jsonplaceholder.typicode.com/users/1')
        .then(data=>setS(data.data))
        .catch(err=>console.log('erorr :' , err.message))

        // use axios by async way
        const ff=async()=>{
            try{
                const data = await axios.get('https://jsonplaceholder.typicode.com/users/1')
                setS(data.data)
            }catch(err){console.log('erorr :' , err.message)}
        };ff()

    },[])

    const jsxResult = s!=null
        ?<>
            <p className='done'>done :)</p>
            <div className='user'>
                <p>id : {s.id}</p>
                <p>name : {s.name}</p>
                <p>username : {s.username}</p>
                <p>email : {s.email}</p>
            </div>
        </>
        :
        <button className='load'>
            loading...
        </button>

    return (
        <div className='container'>
            {jsxResult}
        </div>
    )
}
